import React from "react";
import sculptureStudio from "../../assets/studio.sculpture.jpg";
import Fade from "react-reveal/Fade";
// import drawingStudio from "../../assets/drawingstudio.jpg.JPG";

import "./hero.styles.scss";

const Hero = () => (
  <div className="hero">
    <div className="hero-text">
      <Fade left cascade duration={3000}>
        <div>
          <h1>Lofrano Arts</h1>
          <h4>a universal flow of energy and creativity that connects us all</h4>
        </div>
      </Fade>
    </div>
    <div className="hero-image-container">
      <Fade right delay={500} duration={2000}>
        <img className="hero-image" src={sculptureStudio} alt="sculpture-studio" />
      </Fade>
      {/* <img className="hero-image" src={drawingStudio} alt="drawing-studio" /> */}
    </div>
  </div>
);

export default Hero;
