import { takeEvery, call, put } from "redux-saga/effects";
import GallerActionTypes from "./gallery.types";
import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../firebase/firebase.utils.js";
import { fetchCollectionsSuccess, fetchCollectionsFailure } from "../gallery/";

export function* fetchCollectionsAsync() {
  try {
    const collectionRef = firestore.collection("collections");
    const snapshot = yield collecitonRef.get();
    const collectionsMap = yield call(
      convertCollectionsSnapshotToMap,
      snapshot
    );
    yield put(fetchCollectionsSuccess(collectionsMap));
  } catch (error) {
    yield put(fetchCollectionsFailure(error.message));
  }

  // The above code takes the following code, used in our gallery actions, and converts from Thunk to Saga
  //   collectionRef
  //     .get()
  //     .then((snapshot) => {
  //       const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
  //       dispatch(fetchCollectionsSuccess(collectionsMap));
  //     })
  //     .catch((error) => dispatch(fetchCollectionsFailure(error.message)));
  // };
}

export function* fetchCollectionsStart() {
  yield takeEvery(
    GallerActionTypes.FETCH_COLLECTIONS_START,
    fetchCollectionsAsync
  );
}
