import ShopActionTypes from "./shops.types.js";

const INITIAL_STATE = {
  collections: null,
};

const galleryReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ShopActionTypes.UPDATE_COLLECTIONS:
      return {
        ...state,
        collections: action.payload,
      };
    default:
      return state;
  }
};

export default galleryReducer;
