import React from "react";
import Fade from "react-reveal/Fade";

import sculptureStudio from "../../assets/studio.sculpture.jpg";
import drawingStudio from "../../assets/studio.drawing.jpg";

import "./hero.styles.scss";

const Hero = () => (
  <div className="hero">
    <div className="hero-text">
      <Fade left cascade duration={1000}>
        <div>
          <h1>Lofrano Arts</h1>
          <h4>a universal flow of energy and creativity that connects us all</h4>
        </div>
      </Fade>
    </div>
    <div className="sculpture-image-container">
      <Fade center delay={500} duration={2000}>
        <img className="sculpture-image" src={sculptureStudio} alt="sculpture-studio" />
      </Fade>
    </div>
    <Fade center delay={300} duration={200}>
      <div className="drawing-image-container" style={{
        backgroundImage: `url(${drawingStudio})`
      }}>
    </div>
    </Fade>
  </div>
);

export default Hero;
