import React from "react";
import CollectionItem from "../collection-item/collection-item.component.jsx";
import { Link } from "react-router-dom";

import "./colleciton-preview.styles.scss";

class CollectoinPreview extends React.Component {
  state = {
    isWindowWide: null,
  };

  checkWindowWidth() {
    if (window.innerWidth <= 1200 || window.innerWidth > 800) {
      this.setState({ isWindowWide: false });
    } else if(window.innerWidth <= 800 || window.innerWidth > 1200) {
      this.setState({ isWindowWide: true });
    }
  }

  componentDidMount() {
    this.checkWindowWidth();
    window.addEventListener("resize", this.checkWindowWidth.bind(this));
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.checkWindowWidth.bind(this));
  }

  render() {
    const { title, items } = this.props;
    console.log(this.state.isWindowWide);
    console.log(window.innerWidth);
    return (
      <div className="collection-preview">
        <h1 className="title">
          <Link to={`gallery/${title.toLowerCase()}`}>
            {title.toUpperCase()}
          </Link>
        </h1>
        <div className="preview">
          {this.state.isWindowWide
            ? items
                .filter((item, idx) => idx < 4)
                .map((item) => <CollectionItem key={item.id} item={item} />)
            : items
                .filter((item, idx) => idx < 3)
                .map((item) => <CollectionItem key={item.id} item={item} />)}
        </div>
      </div>
    );
  }
}

export default CollectoinPreview;
